    public static String nextString(Scanner scan)
    {
        String user = "";
        while (!(user.equals("y") || user.equals("n") || user.equals("t")))
        {
            user = scan.nextLine();
            user = user.toLowerCase();
        }
        return user;
    }

    public static String getInput(Scanner scan)
    {
        String user = "";
        while (user.equals("") || user.equals("\n") || user.equals(" "))
        {
            user = scan.nextLine();
            user = user.toLowerCase();
        }
        return user;
    }

    public static String printArray(double[] arr)
    {
        String out = "";
        int counter = 0;
        for (double i: arr)
        {
            out+= i+", ";
        }
        return out;
    }

    public static int arrayToNum(double[] arr)
    {
        double biggest = arr[0];
        int biggestIndex = 0;
        for (int i = 1; i < arr.length; i++)
        {
            if (arr[i]>biggest)
            {
                biggest = arr[i];
                biggestIndex = i;
            }
        }
        return biggestIndex;
    }
